package com.github.EpicSeven.presentation.view;import android.content.Intent;import android.os.Bundle;import android.widget.TextView;import androidx.appcompat.app.AppCompatActivity;import com.github.EpicSeven.R;import com.github.EpicSeven.Singletons;import com.github.EpicSeven.presentation.model.Hero;public class DetailActivity extends AppCompatActivity {    private TextView txtName;    private TextView txtRole;    private TextView txtId;    Intent intent;    @Override    protected void onCreate(Bundle savedInstanceState){        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_detail);        txtName = findViewById(R.id.name_txt);        txtId = findViewById(R.id.id_txt);        txtRole = findViewById(R.id.role_txt);        intent = getIntent();        String heroJson = intent.getStringExtra("HeroKey");        Hero hero= Singletons.getGson().fromJson(heroJson, Hero.class);        showDetail(hero);    }    private void showDetail(Hero hero)    {        txtName.setText(hero.getName());        txtId.setText(hero.getId());        txtRole.setText(hero.getRole());    }}