package com.github.arenaofvalor;import androidx.appcompat.app.AppCompatActivity;import android.content.SharedPreferences;import android.os.Bundle;import com.google.gson.Gson;import com.google.gson.GsonBuilder;import java.util.ArrayList;import java.util.List;import retrofit2.Call;import retrofit2.Callback;import retrofit2.Response;import retrofit2.Retrofit;import retrofit2.converter.gson.GsonConverterFactory;public class MainActivity extends AppCompatActivity{    private List<Hero> AovHeroes = new ArrayList<>();    private SharedPreferences sharedPreferences;    private Gson gson;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_main);        APIcall();    }    private void APIcall(){        Gson gson = new GsonBuilder()        .setLenient()                .create();        Retrofit retrofit = new Retrofit.Builder()                .baseUrl("https://raw.githubusercontent.com/")                .addConverterFactory(GsonConverterFactory.create(gson)).build();        ArenaOfValorAPI arenaOfValorAPI = retrofit.create(ArenaOfValorAPI.class);        Call<ArenaOfValorResponse> call = arenaOfValorAPI.getHeroResponse();        call.enqueue(new Callback<ArenaOfValorResponse>() {            @Override            public void onResponse(Call<ArenaOfValorResponse> call, Response<ArenaOfValorResponse> response) {                if(response.isSuccessful()&& response.body()!= null){                    AovHeroes = response.body().getResults();                    for(Hero H: AovHeroes){                        System.out.println(H.getId()+" "+" "+H.getType()+" "+" "+H.getName());                    }System.out.println(AovHeroes.size());                }                else{                    System.out.println("Fail");                }            }            @Override            public void onFailure(Call<ArenaOfValorResponse> call, Throwable t) {                System.out.println("Fail");            }        });    }}