package com.github.arenaofvalor;import android.content.Context;import android.content.SharedPreferences;import com.github.arenaofvalor.data.ArenaOfValorAPI;import com.google.gson.Gson;import com.google.gson.GsonBuilder;import retrofit2.Retrofit;import retrofit2.converter.gson.GsonConverterFactory;public class Singletons {    private static Gson gsonInstance = null;    private static ArenaOfValorAPI aovInstance;    private static SharedPreferences sharedPreferencesInstance;    public static Gson getGson(){        if(gsonInstance == null) {            return new GsonBuilder()                    .setLenient()                    .create();        }        return gsonInstance;    }    public static ArenaOfValorAPI getArenaOfValorAPI(){        if(aovInstance== null){            Retrofit retrofit = new Retrofit.Builder()                    .baseUrl("https://raw.githubusercontent.com/Yihsuanlee1112/arenaofvalor/master/app/Files/")                    .addConverterFactory(GsonConverterFactory.create(getGson()))                    .build();            aovInstance = retrofit.create(ArenaOfValorAPI.class);        }        return aovInstance;    }    public static SharedPreferences getSharedPreferencesInstance(Context context) {        if (sharedPreferencesInstance == null) {            sharedPreferencesInstance = context.getSharedPreferences("application_esiea", Context.MODE_PRIVATE);        }        return sharedPreferencesInstance;    }}