package com.github.arenaofvalor.presentation.view;import android.content.Intent;import android.os.Bundle;import android.widget.TextView;import androidx.appcompat.app.AppCompatActivity;import com.github.arenaofvalor.R;import com.github.arenaofvalor.Singletons;import com.github.arenaofvalor.presentation.model.Hero;public class DetailActivity extends AppCompatActivity {    private TextView textDetail;    @Override    protected void onCreate(Bundle savedInstanceState){        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_detail);        textDetail = findViewById(R.id.detail_txt);        Intent intent = getIntent();        String heroJson = intent.getStringExtra("heroKey");        Hero hero = Singletons.getGson().fromJson(heroJson, Hero.class);        showDetail(hero);    }    private void showDetail(Hero hero) {        textDetail.setText(hero.getName());    }}